    private void instructorListValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_instructorListValueChanged
        if (!evt.getValueIsAdjusting()) {
            String selection = instructorList.getSelectedValue().toString();
            Name.setText(selection);
            if (selection.equals("Fisher, Gene")) {
                disabilities.setText("No");
                id.setText("gfisher");
                officeNumber.setText("210");
                wtus.setText("12");
            }
            else if (selection.equals("Mammen, Kurt")) {
                disabilities.setText("No");
                id.setText("kmammen");
                officeNumber.setText("228");
                wtus.setText("12");
            }
            else if (selection.equals("Nico, Phillip")) {
                disabilities.setText("No");
                id.setText("pnico");
                officeNumber.setText("205");
                wtus.setText("12");
            }
            else if (selection.equals("Workman, Julie")) {
                disabilities.setText("No");
                id.setText("jworkman");
                officeNumber.setText("206");
                wtus.setText("12");
            }
            else if (selection.equals("Seng, John")) {
                disabilities.setText("No");
                id.setText("jseng");
                officeNumber.setText("231");
                wtus.setText("0");
            }

        }
    }//GEN-LAST:event_instructorListValueChanged

